import {
  IonContent,
  IonIcon,
  IonItem,
  IonLabel,
  IonList,
  IonListHeader,
  IonMenu,
  IonMenuToggle,
  IonNote,
} from '@ionic/react';

import { useLocation } from 'react-router-dom';
import { archiveOutline, archiveSharp, bookmarkOutline, heartOutline, heartSharp, 
  mailOutline, mailSharp, paperPlaneOutline, paperPlaneSharp,


          trashOutline, trashSharp, warningOutline, warningSharp } from 'ionicons/icons';
import './Menu.css';

interface AppPage {
  url: string;
  iosIcon: string;
  mdIcon: string;
  title: string;
}

const appPages: AppPage[] = [
  {
    title: 'Tablero',
    url: '/page/home',
    iosIcon: mailOutline,
    mdIcon: mailSharp
  },
  {
    title: 'Productos',
    url: '/page/products',
    iosIcon: paperPlaneOutline,
    mdIcon: paperPlaneSharp
  },
  {
    title: 'Pedidos',
    url: '/page/orders',
    iosIcon: heartOutline,
    mdIcon: heartSharp
  },
  {
    title: 'Clientes',
    url: '/page/customers',
    iosIcon: archiveOutline,
    mdIcon: archiveSharp
  },
  {
    title: 'Usuarios',
    url: '/page/users',
    iosIcon: trashOutline,
    mdIcon: trashSharp
  },
  {
    title: 'Facturacion',
    url: '/page/Spam',
    iosIcon: warningOutline,
    mdIcon: warningSharp
  },
  {
    title: 'Empresa',
    url: '/page/Spam',
    iosIcon: warningOutline,
    mdIcon: warningSharp
  },
  {
    title: 'Proveedores',
    url: '/page/Spam',
    iosIcon: warningOutline,
    mdIcon: warningSharp
  },
  {
    title: 'Configuracion',
    url: '/page/Spam',
    iosIcon: warningOutline,
    mdIcon: warningSharp
  }
];

const labels = ['Family', 'Friends', 'Notes', 'Work', 'Travel', 'Reminders'];

const Menu: React.FC = () => {
  const location = useLocation();

  return (
    <IonMenu contentId="main" type="overlay">
      <IonContent>
        <IonList id="inbox-list">
          <IonListHeader>Inbox</IonListHeader>
          <IonNote>hi@ionicframework.com</IonNote>
          {appPages.map((appPage, index) => {
            return (
              <IonMenuToggle key={index} autoHide={false}>
                <IonItem className={location.pathname === appPage.url ? 'selected' : ''} routerLink={appPage.url} routerDirection="none" lines="none" detail={false}>
                  <IonIcon aria-hidden="true" slot="start" ios={appPage.iosIcon} md={appPage.mdIcon} />
                  <IonLabel>{appPage.title}</IonLabel>
                </IonItem>
              </IonMenuToggle>
            );
          })}
        </IonList>

        <IonList id="labels-list">
          <IonListHeader>Labels</IonListHeader>
          {labels.map((label, index) => (
            <IonItem lines="none" key={index}>
              <IonIcon aria-hidden="true" slot="start" icon={bookmarkOutline} />
              <IonLabel>{label}</IonLabel>
            </IonItem>
          ))}
        </IonList>
      </IonContent>
    </IonMenu>
  );
};

export default Menu;
