export interface formatProduct {
  id: number;
  name: string;
  description: string;
  image: string;
}
const prueba: formatProduct[] = [
  {
    id: 1,
    name: "Producto 1",
    description: "Producto 1",
    image: "https://ionicframework.com/docs/img/demos/thumbnail.svg",
  },
  {
    id: 2,
    name: "Producto 2",
    description: "Producto 2",
    image: "https://ionicframework.com/docs/img/demos/thumbnail.svg",
  },
];

export function searchDataProducts() {
  localStorage.clear();
  if (!localStorage["products"]) {
    localStorage["products"] = JSON.stringify(prueba);
  }
  let products = localStorage["products"];
  console.log(products);
  products = JSON.parse(products);
  return products;
}

export function removeDataProduct() {}

export function saveDataProduct(product: formatProduct) {
  let products = searchDataProducts();
  products.push(product);
  localStorage["products"] = JSON.stringify(products);
}
