import {
  IonContent,
  IonIcon,
  IonItem,
  IonLabel,
  IonList,
  IonListHeader,
  IonMenu,
  IonMenuToggle,
  IonNote,
} from "@ionic/react";

import { useLocation } from "react-router-dom";
import {homeOutline, homeSharp} from "ionicons/icons";
import "./Menu.css";

interface AppPage {
  url: string;
  iosIcon: string;
  mdIcon: string;
  title: string;
}

const appPages: AppPage[] = [
  {
    title: "Tablero",
    url: "/page/home",
    iosIcon: homeOutline,
    mdIcon: homeSharp,
  },
  {
    title: "Productos",
    url: "/page/products",
    iosIcon: paperPlaneOutline,
    mdIcon: paperPlaneSharp,
  },
  {
    title: "Pedidos",
    url: "/page/orders",
    iosIcon: heartOutline,
    mdIcon: heartSharp,
  },
  {
    title: "Clientes",
    url: "/page/customers",
    iosIcon: archiveOutline,
    mdIcon: archiveSharp,
  },
  {
    title: "Facturacion",
    url: "/page/billing",
    iosIcon: warningOutline,
    mdIcon: warningSharp,
  },
  {
    title: "Proveedores",
    url: "/page/suppliers",
    iosIcon: warningOutline,
    mdIcon: warningSharp,
  },
];

const labels: AppPage[] = [
  {
    title: "Usuarios",
    url: "/page/users",
    iosIcon: trashOutline,
    mdIcon: trashSharp,
  },
  {
    title: "Configuracion",
    url: "/page/setting",
    iosIcon: warningOutline,
    mdIcon: warningSharp,
  },
];

const Menu: React.FC = () => {
  const location = useLocation();

  return (
    <IonMenu contentId="main" type="overlay">
      <IonContent>
        <IonList id="inbox-list">
          <IonListHeader>Principal</IonListHeader>
          <IonNote>Alex Rodriguez</IonNote>
          {appPages.map((appPage, index) => {
            return (
              <IonMenuToggle key={index} autoHide={false}>
                <IonItem
                  lines="none"
                  detail={false}
                  routerDirection="none"
                  routerLink={appPage.url}
                  className={location.pathname === appPage.url ? "selected" : ""}
                >
                  <IonIcon aria-hidden="true" slot="start" ios={appPage.iosIcon} md={appPage.mdIcon} />
                  <IonLabel>{appPage.title}</IonLabel>
                </IonItem>
              </IonMenuToggle>
            );
          })}
        </IonList>

        <IonList id="labels-list">
          <IonListHeader>Otros</IonListHeader>
          {labels.map((label, index) => {
            return (
              <IonMenuToggle key={index} autoHide={false}>
                <IonItem
                  lines="none"
                  detail={false}
                  routerDirection="none"
                  routerLink={label.url}
                  className={location.pathname === label.url ? "selected" : ""}
                >
                  <IonIcon aria-hidden="true" slot="start" icon={bookmarkOutline} />
                  <IonLabel>{label.title}</IonLabel>
                </IonItem>
              </IonMenuToggle>
            );
          })}
        </IonList>
      </IonContent>
    </IonMenu>
  );
};

export default Menu;
