import { productFormat } from "./ProductFormat";
import axios, { AxiosResponse } from 'axios';

//GET
export function searchProductData() {
  if (!localStorage["products"]) {
    axios.get('http://myeleccion.com:8082/api/productos')
      .then((response: AxiosResponse) => {
        localStorage["products"] = JSON.stringify(response.data);
        return JSON.stringify(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener los productos:', error);
      });
    return [];
  }
  let products = localStorage["products"];
  products = JSON.parse(products);
  return products;
}

//DELETE
export function removeProductData(remove: string) {
  let products = searchProductData();
  products = products.filter((product: productFormat) => product.Id_Producto !== remove);
  localStorage["products"] = JSON.stringify(products);
}

//PUT
export function editProductData(edit: productFormat) {
  let products = searchProductData();
  products = products.filter((product: productFormat) => product.Id_Producto !== edit.Id_Producto);
  products.push(edit);
  localStorage["products"] = JSON.stringify(products);
}

//POST
export function saveProductData(product: productFormat) {
  let products = searchProductData();
  products.push(product);
  localStorage["products"] = JSON.stringify(products);
  return true;
}

export function reloadProductData() {
  localStorage.removeItem("products");
  return searchProductData();
}
