/* eslint-disable react/jsx-no-comment-textnodes */
import React, { useEffect, useState } from "react";
import "./../Page.css";

import {
  IonButton,
  IonCard,
  IonCardHeader,
  IonCardSubtitle,
  IonCardTitle,
  IonItem,
  IonLabel,
  IonList,
  IonListHeader,
  IonThumbnail,
} from "@ionic/react";
import { IonButtons, IonContent, IonHeader, IonMenuButton, IonPage, IonTitle, IonToolbar } from "@ionic/react";
import { searchDataProducts, formatProduct } from "./ProductApi";

const Products: React.FC = () => {
  const [dataProducts, setDataProducts] = useState<formatProduct[]>([]);
  useEffect(() => {
    searchProducts();
  }, []);

  const searchProducts = () => {
    let list = searchDataProducts();
    setDataProducts(list);
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>Productos</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen>
        <IonCard>
          <IonTitle>Gestion de Productos</IonTitle>
        </IonCard>
        <IonList inset={true}>
          <IonListHeader color={"primary"}>
            <IonLabel>Listado de Productos</IonLabel>
          </IonListHeader>
          {dataProducts.map((item, index) => {
            const sendUrl = "products/" + item.id;
            return (
              <>
                <IonItem key={index}>
                  <IonThumbnail slot="start">
                    <img alt="Silhouette of mountains" src={item.image} />
                  </IonThumbnail>
                  <IonCardHeader>
                    <IonCardTitle>{item.name}</IonCardTitle>
                    <IonCardSubtitle>{item.description}</IonCardSubtitle>
                  </IonCardHeader>
                  <IonButton slot="end" shape="round" size="default" fill="outline" routerLink={sendUrl}>
                    &gt;
                  </IonButton>
                </IonItem>
              </>
            );
          })}
        </IonList>
      </IonContent>
    </IonPage>
  );
};

export default Products;
