import { productFormat } from "./ProductFormat";
import axios, { AxiosResponse } from 'axios';
import { URL } from "./../../config";

//GET
export function searchProductData(): productFormat[] {
  if (!localStorage["products"]) {
    fetchProductData();
    return [];
  }
  let products = localStorage["products"];
  products = JSON.parse(products);
  return products;
}

//DELETE
export function removeProductData(remove: string): void {
  removeProductDataAPI(remove)
    .then(() => {
      let products: productFormat[] = searchProductData();
      products = products.filter((product: productFormat) => product.Id_Producto !== remove);
      localStorage["products"] = JSON.stringify(products);
    })
    .catch((error) => {
      console.error('Error al eliminar el producto:', error);
    });
}

//PUT
export function editProductData(edit: productFormat): void {
  editProductDataAPI(edit)
    .then(() => {
      let products: productFormat[] = searchProductData();
      products = products.map((product: productFormat) => {
        if (product.Id_Producto === edit.Id_Producto) {
          return edit;
        }
        return product;
      });
      localStorage["products"] = JSON.stringify(products);
    })
    .catch((error) => {
      console.error('Error al editar el producto:', error);
    });
}

//POST
export function saveProductData(product: productFormat): void {
  saveProductDataAPI(product)
    .then(() => {
      let products: productFormat[] = searchProductData();
      products.push(product);
      localStorage["products"] = JSON.stringify(products);
    })
    .catch((error) => {
      console.error('Error al guardar el producto:', error);
    });
}

export function reloadProductData() {
  localStorage.removeItem("products");
  return searchProductData();
}

export async function fetchProductData(): Promise<void> {
  try {
    const response: AxiosResponse = await axios.get(URL, {
      headers: {
        Authorization: localStorage["token"]
      }
    });
    localStorage["products"] = JSON.stringify(response.data);
  } catch (error: any) {
    if (error.response && error.response.status === 403) {
      console.error('Acceso denegado. Redireccionando a /login...');
      localStorage.clear();
      window.location.href = '/login';
    } else {
      console.error('Error al obtener los productos:', error);
    }
  }
}

export async function removeProductDataAPI(remove: string): Promise<void> {
  try {
    await axios.delete(URL, {
      headers: {
        Authorization: localStorage["token"]
      }
    });
  } catch (error) {
    console.error('Error al eliminar el producto:', error);
    throw error;
  }
}

export async function editProductDataAPI(edit: productFormat): Promise<void> {
  try {
    await axios.put(URL, edit, {
      headers: {
        Authorization: localStorage["token"]
      }
    });
  } catch (error) {
    console.error('Error al editar el producto:', error);
    throw error;
  }
}

export async function saveProductDataAPI(product: productFormat): Promise<void> {
  try {
    await axios.post(URL, product, {
      headers: {
        Authorization: localStorage["token"]
      }
    });
  } catch (error) {
    console.error('Error al guardar el producto:', error);
    throw error;
  }
}
