/* eslint-disable react/jsx-no-comment-textnodes */
import React, { useState } from "react";
import "./../Page.css";

import {
  IonBackButton,
  IonButton,
  IonCard,
  IonCol,
  IonIcon,
  IonInput,
  IonItem,
  IonLabel,
  IonList,
  IonListHeader,
  IonRow,
} from "@ionic/react";
import { IonButtons, IonContent, IonHeader, IonTitle, IonToolbar } from "@ionic/react";
import { checkmark } from "ionicons/icons";
import { productFormat } from "./ProductFormat";
import { saveDataProduct } from "./ProductApi";

const ProductAdd: React.FC = () => {
  const [product, setProduct] = useState<any>({
    id: "",
    name: "",
    description: "",
    image: "",
  });

  const save = () => {
    const newProduct: productFormat = {
      id: Math.round(Math.random() * 10000).toString(),
      name: product.name,
      description: product.description,
      image: product.image,
    };
    saveDataProduct(newProduct);
  };
  return (
    <>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton></IonBackButton>
          </IonButtons>
          <IonTitle>Productos</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen>
        <IonCard>
          <IonList inset={true}>
            <IonListHeader color={"primary"}>
              <IonLabel>Agregar un nuevo productos</IonLabel>
            </IonListHeader>
            <IonRow>
              <IonCol>
                <IonItem>
                  <IonLabel>Nombre</IonLabel>
                  <IonInput
                    onIonChange={(e) => (product.name = e.detail.value)}
                    value={product.name}
                    placeholder="Nombre del producto"
                  ></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>
            <IonRow>
              <IonCol>
                <IonItem>
                  <IonLabel>Descripcion</IonLabel>
                  <IonInput
                    onIonChange={(e) => (product.description = e.detail.value)}
                    value={product.description}
                    placeholder="Descripcion del producto"
                  ></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>
            <IonRow>
              <IonCol>
                <IonItem>
                  <IonLabel>Imagen</IonLabel>
                  <IonInput
                    onIonChange={(e) => (product.image = e.detail.value)}
                    value={product.image}
                    placeholder="Imagen"
                  ></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>

            <IonRow>
              <IonCol>
                <IonItem>
                  <IonButton onClick={save} color={"success"} fill="solid" slot="end" size="default">
                    <IonIcon icon={checkmark} /> Guardar
                  </IonButton>
                </IonItem>
              </IonCol>
            </IonRow>
          </IonList>
        </IonCard>
      </IonContent>
    </>
  );
};

export default ProductAdd;
